document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("navbar-default");if(e){const t=document.querySelector('[data-collapse-toggle="navbar-default"]'),n=e.querySelectorAll("a");t?t.addEventListener("click",()=>e.classList.toggle("hidden")):console.warn("Navbar toggle button not found. Toggle functionality might be affected."),n.forEach(a=>a.addEventListener("click",()=>e.classList.add("hidden")))}else console.warn("Navbar element with ID 'navbar-default' not found. Navigation functionality might be affected.")});
